package com.Iu.s2.loops;

public class LoopFor3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//구구단 3단
//				3*1=3
//				3*2=6
//				3*3=9
//				3*4=12
//			
		for(int j=2; j<10;j++) {
			for(int i=1; i<10;i++) {
				System.out.println(j+" * "+i+" = "+ j*i);
			}
		}
	}

}
